select C.TABLE_SCHEMA AS DATABASESCHEMA, T.TABLE_NAME AS TABLENAME,C.COLUMN_NAME AS COLUMNNAME, DATA_TYPE AS COLUMNTYPE, k.CONSTRAINT_TYPE as KEYCOLUMN from INFORMATION_SCHEMA.columns C inner join information_schema.tables T on C.TABLE_NAME=T.TABLE_NAME and C.TABLE_SCHEMA=T.TABLE_SCHEMA inner join information_schema.table_constraints k on k.TABLE_NAME=T.TABLE_NAME WHERE T.TABLE_SCHEMA NOT IN ( 'information_schema','MYSQL','performance_schema','mysql', 'sys') and C.TABLE_SCHEMA = REFEREDSCHEMA order by C.TABLE_SCHEMA, T.TABLE_NAME, C.COLUMN_NAME;